// tabs
let tabImgArray1 = document.querySelectorAll('.tab__img img:first-child');
let tabImgArray2 = document.querySelectorAll('.tab__img img:last-child');
let solContentList = document.querySelectorAll('.sol-content__list');

function activate(e, number) {

  for (let i = 0; i < tabImgArray1.length; i++) {
    tabImgArray1[i].style.display = 'block';
    tabImgArray2[i].style.display = 'none';
  }

  let tabImgFirst = e.target.parentNode.querySelector('.tab__img img:first-child');
  tabImgFirst.style.display = 'none';
  let tabImgLast = e.target.parentNode.querySelector('.tab__img img:last-child');
  tabImgLast.style.display = 'block';

  for (let ii = 0; ii < solContentList.length; ii++) {
    solContentList[ii].style.display = 'none';
  }
  solContentList[number].style.display = 'block';
}


// slick
if ($('.reviews__list').length) {
  reviewListSlick();
}

$(window).resize(function () {
  if ($('.reviews__list').length) {
    reviewListSlick();
  }
})

function reviewListSlick() {

  $('.reviews__list').slick({
    accessibility: false,
    arrows: false,
    dots: true,
    appendDots: $('.reviews__dots'),
    dotsClass: 'reviews__dots-list',
    responsive: [{
      breakpoint: 577,
      settings: "unslick"
    }]
  });

  if ($('.reviews__dots-list li button').length) {
    $('.reviews__dots-list li button').empty();
  }
}
